for (const item of $input.all()) {
  const linkedinUrl = item.json["Linkedin URL"];
  if (linkedinUrl) {
    // Quitar barra final si existe y quedarnos con el último segmento
    const PeopleName = linkedinUrl.replace(/\/$/, '').split('/').pop();
    item.json.PeopleName = PeopleName;
  }
}

return $input.all();

